return array (
  'article_titles' =>
  array (
    't0' => '<strong>Was bei Bonsum anders und neu ist?<br>Der Kunde wird nicht zum gl&auml;sernen Konsumenten</strong>',
    't1' => '1. Deine Daten, nicht unsere!',
    't2' => '2. Nutzung Deiner Daten f&uuml;r individuelle Angebote, Werbung und Marktforschung / K&auml;uferprofile',
    't3' => '3. Keine &Uuml;bermittlung Deiner Daten an Dritte',
    't4' => '4. L&ouml;schung der Daten',
    't5' => '5. E-Mails aus und in Europa',
    't6' => '6. Verwendung von Social Plugins',
